for(var buyWindow=document.querySelectorAll(".buy"),modalBuy=document.querySelector(".added-product"),closeBuy=document.querySelector(".close-order"),i=0;i<buyWindow.length;i++)buyWindow[i].addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.remove("visually-hidden")});closeBuy.addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.add("visually-hidden")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalBuy.classList.contains("added-product")&&(e.preventDefault(),modalBuy.classList.add("visually-hidden"))});
