for(var buyWindow=document.querySelectorAll(".buy"),modalBuy=document.querySelector(".added-product"),closeBuy=document.querySelector(".close-order"),cancelBuy=modalBuy.querySelector(".continue-shopping"),countBuy=document.querySelector(".cart"),counter=countBuy.querySelector(".count"),i=0;i<buyWindow.length;i++)buyWindow[i].addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.remove("visually-hidden"),countBuy.classList.add("filled");var n=Number(counter.innerHTML);counter.innerHTML=n+=1});closeBuy.addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.add("visually-hidden")}),cancelBuy.addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.add("visually-hidden")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalBuy.classList.contains("added-product")&&(e.preventDefault(),modalBuy.classList.add("visually-hidden"))});
